def generate_prediction(name, count=3):
    all_cards = CARDS[:]  # –ø–æ–ª–Ω—ã–π —Å–ø–∏—Å–æ–∫ –∫–∞—Ä—Ç
    selected_cards = random.sample(all_cards, min(count, len(all_cards)))
    messages = [f"üîÆ –Ø –≤–∏–∂—É, {name}‚Ä¶\n"]

    for card in selected_cards:
        meaning = random.choice(CARD_MEANINGS.get(card, ["–¢–∞–∏–Ω—Å—Ç–≤–µ–Ω–Ω–∞—è –∫–∞—Ä—Ç–∞ üåå"]))
        emojis = random.sample(
            ["üåô", "‚ú®", "üî•", "üí´", "üåä", "‚ö°", "üåÄ", "üåü", "üåø", "üíÄ", "üåà", "üïØÔ∏è", "üíé", "üçÇ", "üå∫", "üçÉ"], 2
        )
        messages.append(f"{emojis[0]} {card}: {meaning} {emojis[1]}\n")

    messages.append(
        "üåà –°—É–¥—å–±–∞ —à–µ–ø—á–µ—Ç: –≤–ø–µ—Ä–µ–¥–∏ –º–æ–≥—É—Ç –±—ã—Ç—å –∫–∞–∫ –∏—Å–ø—ã—Ç–∞–Ω–∏—è, —Ç–∞–∫ –∏ —Ä–∞–¥–æ—Å—Ç—å, "
        "–ø—Ä–∏—Å–ª—É—à–∏–≤–∞–π—Å—è –∫ —Å–≤–æ–∏–º —Ä–µ—à–µ–Ω–∏—è–º –∏ –≤–Ω—É—Ç—Ä–µ–Ω–Ω–µ–º—É –≥–æ–ª–æ—Å—É üíñ"
    )
    return "\n".join(messages)

